"""
Grailed 商品爬虫
搜索 URL 格式: https://www.grailed.com/shop?query=keyword
无 page 参数，仅按 query 爬取，每个关键词保存一份 HTML
"""
import asyncio
import re
from datetime import datetime
from pathlib import Path
from urllib.parse import urlencode, quote

from playwright.async_api import async_playwright

GRAILED_SHOP_BASE = "https://www.grailed.com/shop"


def build_search_url(query):
    """构建 Grailed 搜索 URL（无 page 参数）"""
    return f"{GRAILED_SHOP_BASE}?query={quote(query)}"


class GrailedCrawler:
    """Grailed 爬虫类"""

    def __init__(self, headless=True, browser_channel=None):
        self.headless = headless
        self.browser_channel = browser_channel
        self.playwright = None
        self.browser = None
        self.context = None
        self.page = None

    async def init_browser(self):
        if not self.playwright:
            self.playwright = await async_playwright().start()
        launch_opts = {
            "headless": self.headless,
            "args": [
                "--disable-blink-features=AutomationControlled",
                "--disable-dev-shm-usage",
                "--no-sandbox",
                "--disable-gpu",
                "--disable-software-rasterizer",
            ],
        }
        if self.browser_channel:
            launch_opts["channel"] = self.browser_channel
        self.browser = await self.playwright.chromium.launch(**launch_opts)
        self.context = await self.browser.new_context(
            viewport={"width": 1920, "height": 1080},
            user_agent="Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36",
        )
        self.page = await self.context.new_page()
        await self.page.add_init_script("""
            Object.defineProperty(navigator, 'webdriver', { get: () => undefined });
        """)

    async def close(self):
        if self.page:
            try:
                await self.page.close()
            except Exception:
                pass
        if self.context:
            try:
                await self.context.close()
            except Exception:
                pass
        if self.browser:
            try:
                await self.browser.close()
            except Exception:
                pass
        if getattr(self, "playwright", None):
            try:
                await self.playwright.stop()
            except Exception:
                pass

    async def scroll_to_load(self, scroll_times=5):
        """简单固定次数滚动（保留兼容）"""
        for _ in range(scroll_times):
            await self.page.evaluate("window.scrollBy(0, window.innerHeight)")
            await asyncio.sleep(0.8)
        await self.page.evaluate("window.scrollTo(0, 0)")
        await asyncio.sleep(0.3)

    async def scroll_until_all_loaded(
        self,
        max_scrolls=200,
        scroll_pause=0.5,
        no_new_content_rounds=6,
        log_interval=25,
    ):
        """
        无限滚动：持续滚动直到没有新内容加载或达到最大次数。
        Grailed 需滚动很久才能加载全部商品，用此方法尽量拉全。
        """
        last_height = 0
        same_height_count = 0
        for i in range(max_scrolls):
            await self.page.evaluate("window.scrollBy(0, window.innerHeight)")
            await asyncio.sleep(scroll_pause)
            try:
                height = await self.page.evaluate(
                    "document.documentElement.scrollHeight"
                )
                scroll_top = await self.page.evaluate("window.scrollY")
                client_height = await self.page.evaluate("window.innerHeight")
                at_bottom = scroll_top + client_height >= height - 50
                if height == last_height:
                    same_height_count += 1
                    if at_bottom and same_height_count >= no_new_content_rounds:
                        print(f"      滚动结束（已到底且高度未变 x{same_height_count}）")
                        break
                    if same_height_count >= no_new_content_rounds * 2:
                        print(f"      滚动结束（高度未变 x{same_height_count}）")
                        break
                else:
                    same_height_count = 0
                last_height = height
            except Exception:
                pass
            if (i + 1) % log_interval == 0:
                print(f"      已滚动 {i + 1}/{max_scrolls} 次...")
        await self.page.evaluate("window.scrollTo(0, 0)")
        await asyncio.sleep(0.3)


async def crawl_and_save_html(
    queries,
    output_dir="grailed_data",
    headless=True,
    browser_channel=None,
):
    """
    按关键词列表爬取 Grailed，每个 query 访问一个 URL，保存 HTML。
    无分页，仅保存当前请求得到的 HTML。
    """
    output_dir = Path(output_dir)
    output_dir.mkdir(parents=True, exist_ok=True)
    crawler = GrailedCrawler(headless=headless, browser_channel=browser_channel)

    try:
        await crawler.init_browser()
        for idx, query in enumerate(queries, 1):
            url = build_search_url(query)
            print(f"[{idx}/{len(queries)}] {query} -> {url}")
            try:
                await crawler.page.goto(
                    url, wait_until="domcontentloaded", timeout=60000
                )
                await asyncio.sleep(2)
                # 无限滚动直到加载完所有商品（Grailed 需滚动很久）
                await crawler.scroll_until_all_loaded(
                    max_scrolls=200,
                    scroll_pause=0.5,
                    no_new_content_rounds=6,
                    log_interval=25,
                )
                html_content = await crawler.page.content()
                safe_name = re.sub(r'[<>:"/\\|?*]', "_", query.strip())[:80]
                ts = datetime.now().strftime("%Y%m%d_%H%M%S")
                filename = f"{safe_name}_{ts}.html"
                filepath = output_dir / filename
                filepath.write_text(html_content, encoding="utf-8")
                print(f"    已保存: {filepath.name}")
            except Exception as e:
                print(f"    错误: {e}")
            await asyncio.sleep(1)
    finally:
        await crawler.close()
    print("爬取结束")


def _safe_query_name(query):
    """与保存文件名一致的规范化，用于比较是否已爬取"""
    return re.sub(r'[<>:"/\\|?*]', "_", query.strip())[:80]


def get_crawled_queries(data_dir="grailed_data"):
    """从已保存的 HTML 文件名中提取已爬取的 query（用于断点续爬）"""
    data_path = Path(data_dir)
    if not data_path.exists():
        return set()
    seen = set()
    for f in data_path.glob("*.html"):
        m = re.match(r"^(.+)_\d{8}_\d{6}\.html$", f.name)
        if m:
            seen.add(m.group(1))
    return seen


def filter_queries(queries_list, crawled_queries):
    """过滤掉已爬取的 query（按保存时的 safe_name 比较）"""
    crawled_set = set(crawled_queries)
    uncrawled = [q for q in queries_list if _safe_query_name(q) not in crawled_set]
    crawled = [q for q in queries_list if _safe_query_name(q) in crawled_set]
    return uncrawled, crawled


